import React, { Component } from "react";
import PropTypes from "prop-types";

export default class TodoEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.todoSaveBtn = this.todoSaveBtn.bind(this);
    this.enterKeyPress = this.enterKeyPress.bind(this);
  }

  handleChange(e) {
    this.setState({
      data: e.target.value
    });
  }

  todoSaveBtn() {
    const TodoData = {
      data: this.state.data
    };

    this.props.todoSave(TodoData);
    console.console.log(this.state.data);
    this.setState({
      data: ""
    });
  }

  enterKeyPress(e) {
    if (e.charCode === 13) {
      this.todoSaveBtn();
    }
  }

  render() {
    return (
      <div className="TDEdit">
        <h2>신규할일</h2>
        <textarea
          type="text"
          name="data"
          placeholder="오늘은 무슨 일을 해볼까?"
          value={this.state.data}
          onChange={this.handleChange}
          onKeyPress={this.enterKeyPress}
        />
        <button onClick={this.todoSaveBtn}>생성</button>
      </div>
    );
  }
}

TodoEdit.propTypes = {
  todoSave: PropTypes.func
};
TodoEdit.defaultProps = {
  todoSave: () => {
    console.error("저장안됨");
  }
};
