import React, { Component } from "react";
import PropTypes from "prop-types";

import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import red from "@material-ui/core/colors/red";
import MoreVertIcon from "@material-ui/icons/MoreVert";

import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import { TextField } from "@material-ui/core";

const ITEM_HEIGHT = 48;

const styles = theme => ({
  card: {
    maxWidth: 400
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  actions: {
    display: "flex"
  },
  expand: {
    transform: "rotate(0deg)",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest
    }),
    marginLeft: "auto",
    [theme.breakpoints.up("sm")]: {
      marginRight: -8
    }
  },
  expandOpen: {
    transform: "rotate(180deg)"
  },
  avatar: {
    backgroundColor: red[500]
  },

  root: {
    display: "flex"
  },
  paper: {
    marginRight: theme.spacing.unit * 2
  }
});

export default class TodoContentChild extends Component {
  constructor(props) {
    super(props);
    this.state = {
      index: 0,
      updateSwitch: false
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleIndexReturn = this.handleIndexReturn.bind(this);
    this.changeUpdate = this.changeUpdate.bind(this);
  }

  handleClick(e) {
    this.setState({ anchorEl: e.currentTarget });
    //event.target 은 span태그 event.currentTarget 은 div 반환
  }

  changeUpdate() {
    this.setState({
      updateSwitch: !this.state.updateSwitch
    });
  }

  handleIndexReturn() {
    this.props.onRemove(this.state.index);
  }

  handleClose() {
    this.setState({ anchorEl: null });
  }

  checkUpdateOut() {
    {
      if (this.state.updateSwitch) {
        this.props.onRemove;
      } else {
        changeUpdate();
      }
    }
  }

  render() {
    const textView = <Typography>{this.props.contact.data}</Typography>;
    const textField = (
      <TextField
        className="TDTextField"
        name="data"
        rows="4"
        margin="normal"
        variant="outlined"
        placeholder="수정을 해보쟈"
        value={this.props.contact.data}
        onChange={this.handleChange}
        onKeyPress={this.enterKeyPress}
      />
    );
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <Card className="TDChild">
        <CardHeader
          action={
            <IconButton onClick={this.handleClick}>
              <MoreVertIcon />
            </IconButton>
          }
          title="오늘 할일"
        />
        <Menu
          id="long-menu"
          anchorEl={anchorEl}
          open={open}
          onClose={this.handleClose}
          PaperProps={{
            style: {
              maxHeight: ITEM_HEIGHT * 4.5,
              width: 100
            }
          }}
        >
          <MenuItem onClick={this.changeUpdate}>
            {" "}
            {this.state.updateSwitch ? "수정" : "수정확인"}
          </MenuItem>
          <MenuItem onClick={this.checkUpdateOut}>
            {" "}
            {this.state.updateSwitch ? "삭제" : "수정취소"}
          </MenuItem>
        </Menu>
        <CardContent>
          {this.state.updateSwitch ? textView : textField}
        </CardContent>
      </Card>
    );
  }
}
TodoContentChild.defaultProps = {
  key: 1,
  contact: {
    data: ""
  },
  onRemove: () => {
    console.error("제거가 안되넹");
  }
};
TodoContentChild.propTypes = {
  contact: PropTypes.object,
  index: PropTypes.number,
  onRemove: PropTypes.func
};
