import React, { Component } from "react";
import PropTypes from "prop-types";
import TextField from '@material-ui/core/TextField';
import AlertDialog from "./AlertDialog";

import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';

function Transition(props) {
  return <Slide direction="up" {...props} />;
}

export default class TodoEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dialogopen: false,
      data: "",
      check: false,
      alert: "할일 적어주세요"
    };
    this.handleChange = this.handleChange.bind(this);
    this.todoSaveBtn = this.todoSaveBtn.bind(this);
    this.enterKeyPress = this.enterKeyPress.bind(this);
    this.todoOutBtn = this.todoOutBtn.bind(this);
  }

  handleClickOpenDialog = () => {
    this.setState({ dialogopen: true });
  };

  handleCloseDialog = () => {
    this.setState({ dialogopen: false });
  };

  handleChange(e) {
    this.setState({
      data: e.target.value
    });
  }

  todoSaveBtn() {
    const TodoData = {
      data: this.state.data
    };
    if (this.state.data == "") {

    } else {
      this.props.todoSave(TodoData);
      console.log(this.state.data);
      this.setState({
        data: ""
      });
    }

  }

  todoOutBtn() {
    const outCheck = {
      check: this.state.check
    };

    this.props.todoOut(outCheck);
    console.log(this.state.check);
    this.setState({
      check: false
    });
  }

  enterKeyPress(e) {
    if (e.charCode === 13) {
      this.todoSaveBtn();
    }
  }

  render() {
    return (
      <div className="TDEdit">
        <h2>신규할일</h2>
        <TextField
          name="data"
          rows="4"
          margin="normal"
          variant="outlined"
          placeholder={this.state.alert}
          value={this.state.data}
          onChange={this.handleChange}
          onKeyPress={this.enterKeyPress}
        />
        <button onClick={this.todoSaveBtn}
          onClick={this.handleClickOpenDialog}>생성</button>
        <Dialog
          open={this.state.open}
          TransitionComponent={Transition}
          keepMounted
          onClose={this.handleClose}
          aria-labelledby="alert-dialog-slide-title"
          aria-describedby="alert-dialog-slide-description"
        >
          <DialogTitle id="alert-dialog-slide-title">
            {"Use Google's location service?"}
          </DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-slide-description">
              Let Google help apps determine location. This means sending anonymous location data to
              Google, even when no apps are running.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Disagree
            </Button>
            <Button onClick={this.handleClose} color="primary">
              Agree
            </Button>
          </DialogActions>
        </Dialog>
        <button onClick={this.todoOutBtn}>취소</button>
      </div>
    );
  }
}

TodoEdit.propTypes = {
  todoSave: PropTypes.func,
  todoOut: PropTypes.func
};
TodoEdit.defaultProps = {
  todoSave: () => {
    console.error("저장안됨");
  },
  todoOut: () => {
    console.error("안나가짐");
  }
};
