import React, { Component } from "react";
import PropTypes from "prop-types";

export default class TodoEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.todoSave = this.todoSave.bind(this);
    this.enterKeyPress = this.enterKeyPress.bind(this);
  }

  handleChange(e) {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  }

  todoSave() {
    const TodoData = {
      data: this.state.data
    };

    this.props.todoSaveP(TodoData);

    this.setState({
      data: ""
    });
    this.nameInput.focus();
  }

  enterKeyPress(e) {
    if (e.charCode === 13) {
      this.todoSave();
    }
  }

  render() {
    return (
      <div className="TDEdit">
        <h2>신규할일</h2>
        <textarea
          type="text"
          name="dataTextarea"
          placeholder="오늘은 무슨 일을 해볼까?"
          value={this.state.data}
          onChange={this.handleChange}
          ref={ref => {
            this.nameInput = ref;
          }}
          onKeyPress={this.enterKeyPress}
        />
        <button onClick={this.todoSave}>생성</button>
      </div>
    );
  }
}

TodoEdit.propTypes = {
  todoSaveP: PropTypes.func
};
TodoEdit.defaultProps = {
  todoSaveP: () => {
    console.error("저장안됨");
  }
};
