import React, { Component } from "react";
import TodoEdit from "./TodoEdit";
import PropTypes from "prop-types";

export default class TodoContentParent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedKey: -1,
      newEdit: false,
      TodoData: [
        {
          Data: "밥먹기"
        },
        { Data: "잠자기" }
      ]
    };
    this.onEditCheck = this.onEditCheck.bind(this);
  }

  onEditCheck(e) {
    if (!this.state.newEdit) {
      this.setState({
        newEdit: !this.state.newEdit
      });
      console.log("x");
    } else {
      this.setState({
        newEdit: !this.state.newEdit
      });
      console.log("ㅇ");
    }
  }

  render() {
    return (
      /* 새로 만드는 부분*/
      <div className="TDParent">
        <h1>나의 할일들</h1>
        <button onClick={this.onEditCheck} className="button">
          추가
        </button>
        <div>{this.state.newEdit ? <TodoEdit /> : <div />}</div>
      </div>
    );
    /* 뷰 부분*/
    const mapToComponents = data => {
      return data.map((contact, i) => {
        return (
          <ContactInfo
            contact={contact}
            key={i}
            onClick={() => this.handleClick(i)}
          />
        );
      });
    };
  }
}
TodoContentParent.defaultProps = {
  contact: {},
  onEditCheck: () => {
    console.error("버튼채크 오류");
  }
};

TodoContentParent.propTypes = {
  contact: PropTypes.object,
  onEditCheck: PropTypes.func
};
