import React, { Component } from "react";
import PropTypes from "prop-types";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Slide from "@material-ui/core/Slide";
import AlertDialog from "./AlertDialog";

function Transition(props) {
  return <Slide direction="up" {...props} />;
}

export default class TodoEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      openDialog: false,
      data: "",
      check: false,
      alert: "할일 적어주세요"
    };
    this.handleChange = this.handleChange.bind(this);
    this.todoSaveBtn = this.todoSaveBtn.bind(this);
    this.enterKeyPress = this.enterKeyPress.bind(this);
    this.todoOutBtn = this.todoOutBtn.bind(this);
    this.handleClickOpen = this.handleClickOpen.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  handleClickOpen() {
    this.setState({ openDialog: true });
  }

  handleClose() {
    this.setState({ openDialog: false });
  }
  handleChange(e) {
    this.setState({
      data: e.target.value
    });
  }

  todoSaveBtn() {
    const TodoData = {
      data: this.state.data
    };
    if (this.state.data === "" || this.state.data === null) {
      this.setState({
        openDialog: true
      });
    } else {
      this.props.todoSave(TodoData);
      this.setState({
        data: ""
      });
    }
  }

  todoOutBtn() {
    const outCheck = {
      check: this.state.check
    };

    this.props.todoOut(outCheck);
    console.log(this.state.check);
    this.setState({
      check: false
    });
  }

  enterKeyPress(e) {
    if (e.charCode === 13) {
      this.todoSaveBtn();
    }
  }

  render() {
    return (
      <div className="TDEdit">
        <h2>신규할일</h2>
        <TextField
          name="data"
          rows="4"
          margin="normal"
          variant="outlined"
          placeholder={this.state.alert}
          value={this.state.data}
          onChange={this.handleChange}
          onKeyPress={this.enterKeyPress}
        />
        <AlertDialog blackChecker={this.state.openDialog} />
        <p>
          <Button
            variant="contained"
            color="primary"
            onClick={this.todoSaveBtn}
          >
            생성
          </Button>
          <Button variant="contained" color="primary" onClick={this.todoOutBtn}>
            취소
          </Button>
        </p>
      </div>
    );
  }
}
TodoEdit.propTypes = {
  todoSave: PropTypes.func,
  todoOut: PropTypes.func
};
TodoEdit.defaultProps = {
  todoSave: () => {
    console.error("저장안됨");
  },
  todoOut: () => {
    console.error("안나가짐");
  }
};
