import React, { Component } from 'react';
import TodoContent from './TodoContent';

export default class TodoContentParent extends Component {

    constructor(props){
        super(props);
        this.state = {
            newEdit: true
        };
    }

    onEdit(){
        if(!this.state.isEdit){
            this.setState({
            newEdit: !this.state.isEdit
        });
    }
}

    render() {

        return (
            <div className='TDParent'>
                <h1>나의 할일들</h1>
                <button onClick={this.props.onEdit} className='button'>추가</button>
            <div>{this.state.isEdit ?  <TodoContent/> : 'Edit'}</div>    

            </div>
        )
    }
}
