import React, { Component } from "react";
import PropTypes from "prop-types";
import TextField from '@material-ui/core/TextField';

export default class TodoEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: "",
      check: false,
      alert: "할일 적어주세요"
    };
    this.handleChange = this.handleChange.bind(this);
    this.todoSaveBtn = this.todoSaveBtn.bind(this);
    this.enterKeyPress = this.enterKeyPress.bind(this);
    this.todoOutBtn = this.todoOutBtn.bind(this);
  }

  handleChange(e) {
    this.setState({
      data: e.target.value
    });
  }

  todoSaveBtn() {
    const TodoData = {
      data: this.state.data
    };
    if (this.state.data == "") {
      this.state.data = "할일을 적어주세요"
    } else {
      this.props.todoSave(TodoData);
      console.log(this.state.data);
      this.setState({
        data: ""
      });
    }

  }

  todoOutBtn() {
    const outCheck = {
      check: this.state.check
    };

    this.props.todoOut(outCheck);
    console.log(this.state.check);
    this.setState({
      check: false
    });
  }

  enterKeyPress(e) {
    if (e.charCode === 13) {
      this.todoSaveBtn();
    }
  }

  render() {
    return (
      <div className="TDEdit">
        <h2>신규할일</h2>
        <TextField
          name="data"
          rows="4"
          margin="normal"
          variant="outlined"
          placeholder={this.state.alert}
          value={this.state.data}
          onChange={this.handleChange}
          onKeyPress={this.enterKeyPress}
        />
        <button onClick={this.todoSaveBtn}>생성</button>
        <button onClick={this.todoOutBtn}>취소</button>
      </div>
    );
  }
}

TodoEdit.propTypes = {
  todoSave: PropTypes.func,
  todoOut: PropTypes.func
};
TodoEdit.defaultProps = {
  todoSave: () => {
    console.error("저장안됨");
  },
  todoOut: () => {
    console.error("안나가짐");
  }
};
