import React, { Component } from "react";

export default class TodoEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: ""
    };
  }

  todoSave() {
    const newTodoData = {
      data: this.state.data
    };

    this.props.onCreate(newTodoData);

    this.setState({
      data: ""
    });
    this.nameInput.focus();
  }

  render() {
    return (
      <div className="TDEdit">
        <h2>신규할일</h2>
        <textarea
          type="text"
          name="data"
          placeholder="name"
          value={this.state.name}
          onChange={this.handleChange}
          ref={ref => {
            this.nameInput = ref;
          }}
        />
        <button>생성</button>
      </div>
    );
  }
}
